mapper.update:
UPDATE board
SET title =
#{title}, contents = #{contents} , modified_at =
now(), travel_spot = #{travelSpot}, travel_date = #{travelDate}, max_count = #{maxCount}
WHERE id = #{id}

UPDATE board
SET hits = hits + 1
WHERE id = #{id}

UPDATE board
SET current_count = current_count + 1
WHERE id = #{id}

UPDATE board
SET current_count = current_count - 1
WHERE id = #{id}
